INSERT INTO `products` (`id`, `name`, `volume`, `description`, `composition`, `image`, `price`, `subcategory`, `active`, `sell`) VALUES
(1, 'Ребра бараньи Эколь халяль охлажденные', '0.5 кг', 'Ребрышки бараньи Халяль – мясо высочайшего качества. Продукт охлаждается и упаковывается в вакуум, чтобы максимально сохранить свой вкус и аромат. Компания-производитель отбирает только лучших молодых барашков для изготовления ребрышек, мясо отличается мягкостью, сочностью и нежностью. Выбирайте различные способы приготовления по вашему усмотрению: мясо можно тушить, жарить, запекать, а также готовить на гриле. В любом случае результат вас порадует. Для получения лучшего вкуса можно предварительно замариновать ребрышки.', 'Баранина', 'd52ae41fb16.jpg', '210.53', 1, b'1', 7),
(2, 'Ребрышки свиные Мираторг Деликатесные охлажденные', '450 г', 'АПХ «Мираторг» является крупнейшим отечественным производителем свинины и ведущим оператором на рынке мяса и полуфабрикатов в России, благодаря новейшим технологиям, высокому уровню автоматизации и биологической безопасности ферм. Средний вес упаковки - 450г.', 'Свинина', '59a6848ffbf17.jpg', '295.06', 1, b'1', 0),
(3, 'Грудинка говяжья Эколь халяль охлажденная', '0.67 кг', 'Грудинка Говяжья Эколь– это натуральный продукт, который станет прекрасным украшением праздничного и повседневного стола. Обладает сочным вкусом свежего высококачественного мяса. Идеально подходит для приготовления на гриле, сковороде, кастрюле и духовом шкафу. К данному блюду рекомендуется подавать красное вино. Прекрасно сочетается со вкусом жареного и отварного картофеля. Вкус говядины отлично раскроют базилик, орегано, кориандр, мускатный орех и другие пряности.', 'Говядина мясокостная', '29a0224bc4f.jpg', '296.88', 1, b'1', 0),
(4, 'Рулька свиная со специями в маринаде Мираторг охлажденная', '1,4 кг', 'АПХ «Мираторг» является крупнейшим отечественным производителем свинины и ведущим оператором на рынке мяса и полуфабрикатов в России, благодаря новейшим технологиям, высокому уровню автоматизации и биологической безопасности ферм.', 'Рулька свиная, вода питьевая, соль поваренная пищевая, натуральные специи и пряности: лук, черный перец, чеснок, аромат дыма, орегано, базилик, сельдерей, розмарин, тимьян, томат, шалфей, лавровый лист, кориандр.', 'f8859ed44589.jpg', '300.72', 1, b'1', 4),
(5, 'Шейка баранья Эколь халяль охлажденная', '1 кг', 'Компания «Эколь» производит халяльные мясные продукты и одновременно с этим является одним из лидеров по производству высококачественных мясных продуктов в России.', 'Баранина порционная, мясокостная', '15d946f5d.jpg', '350.88', 1, b'1', 0),
(6, 'Карбонад свиной Мираторг без кости', '1.1 кг', 'Свиной карбонад или мясо карбонад – это, как правило, свинина, которая приготовленная особым образом и предназначенная для длительного хранения.', 'Свинина', 'fa36fa5cd6.jpg', '380.00', 1, b'1', 20),
(7, 'Баранина для плова Эколь халяль охлажденная', '1 кг', 'Баранина для плова станет отличным ингредиентом для создания аппетитного второго блюда. Приготовьте настоящий плов с сочным мясом и с характерным жирком. Баранина также подойдет для приготовления насыщенного мясного бульона.', 'Баранина', 'f43efe3b471.jpg', '390.60', 1, b'1', 1),
(8, 'Говядина Эколь по-деревенски халяль охлажденная', '1.2 кг', 'Говядина по-деревенски – это сочная говядина, которая станет идеальной основой для приготовления сытных мясных блюд и супов.Мясо не содержит кости и продается крупным куском. Вы сможете приготовить классические мясные блюда на обед или ужин. Сочное свежее мясо говядины обладает высокими питательными свойствами. Источник белка и отличного заряда бодрости.', 'Говядина бескостная', '68203d99071e.jpg', '553.14', 1, b'1', 4),
(9, 'Огузок МИРАТОРГ говяжий мраморный', '0.97 кг', 'Огузок говяжий МИРАТОРГ мраморный, 1 кг – это вкусное и полезное мясо для приготовления незабываемых блюд. Полностью натуральный продукт уже отделен от кости и кожи, чтобы вы могли сразу приступить к созданию кулинарного шедевра. Говядина является неотъемлемой частью полноценного питания, так как отличается высоким содержанием белка. Огузок подходит для жарки и запекания, но лучше всего раскрывает свои вкусовые свойства в процессе тушения.', 'огузок говяжий', 'f81ec84a782ff767.jpg', '643.69', 1, b'1', 0),
(10, 'Карбонад Мираторг свиной без кости охлажденный', '2,7 кг', 'Карбонад свиной бескостный Мираторг, охлажденный в вакууме – это изысканный мясной деликатес. Нежность и пряный вкус сбалансированного сочетания специй не оставят равнодушным ни одного гурмана. Карбонад свиной Мираторг станет главной мясной закуской званого вечера или семейного торжества. Карбонад Мираторг изготавливают на современной производственной базе, оснащенной передовым оборудованием. Вся свинина проходит жесткий контроль качества и тщательную санитарную проверку. Такой подход позволяет компании и ее потребителям быть уверенными в качестве продукции.', 'Свинина', '40fcfa246b.jpg', '871.56', 1, b'1', 0),
(11, 'Шейка свиная Останкино охлажденная', '2,8 кг', 'ОАО «Останкинский мясоперерабатывающий комбинат» (ОАО «ОМПК») - ведущий производитель продуктов мясопереработки и полуфабрикатов в центральной России. «Останкино» придерживается международных стандартов в производстве продукции, которая благодаря своему качеству, натуральности и вкусу пользуется признанием среди покупателей.', 'Свинина', 'e8df46a41e2.jpg', '1273.44', 1, b'1', 0),
(12, 'Лопатка Мираторг свиная без кости охлажденная', '6 кг', 'Полуфабрикат мясной из свининыны крупнокусковой бескостный категории Б.', 'Свинина', '0b7f666909.jpg', '1648.80', 1, b'1', 0),
(13, 'Чак ролл МИРАТОРГ говяжий', '5,1 кг', 'Чак ролл – часть самой длинной мышцы, выделяющийся с первых трех ребер и первых трех грудных позвонков. При изготовлении чак ролла мы удаляем грубые сухожилия, оставляя при этом жировую прослойку сверху не более 3-5 мм. За счет этого мясо становится более сочным и нежным.', 'Говядина', '312364b0519.jpg', '3882.38', 1, b'1', 0),
(14, 'Крылышко цыпленка бройлера ТМ ПЕТЕЛИНКА', '1 кг', 'Крылышко цыпленка бройлера ПЕТЕЛИНКА', 'Крылышко цыпленка бройлера', 'f3033d0fa5eacc.jpg', '154.80', 3, b'1', 0),
(15, 'Цыплята КУРИНОЕ ЦАРСТВО', '1,5 кг', 'Цыплята КУРИНОЕ  ЦАРСТВО, 1кг отличаются нежностью и сочностью мяса. Продукт делают из тушки молодой птицы бройлерной породы. Цыпленок достигает уже значительных размеров, но мясо еще не успевает приобрести взрослую форму, оставаясь настоящим деликатесом. Не пытайтесь повторно заморозить продукт. Охлаждение происходит по специальной технологии, чтобы все полезные и питательные вещества мяса были сохранены и не потеряли свои свойства. Продукт не содержит ГМО и консерванты', 'Цыпленок', '22e70edad2e.jpg', '162.75', 3, b'1', 2),
(16, 'Бедро куриное Петелинка Особое охлажденное', '1,1 кг', 'Куры для производства бедра куриного ПЕТЕЛИНКА Особого выращиваются на оборудованной всем необходимым птицефабрике. Их кормят специальными, не содержащими ГМО, натуральными кормами, что повышает качество мяса и только прибавляет ему ценных для человеческого организма свойств. ', 'Бедро куриное', '618c7eb63.jpg', '170.27', 3, b'1', 0),
(17, 'Голень куриная ЯСНЫЕ ЗОРИ охлажденная на подложке', '900 г', 'Голень куриная ЯСНЫЕ ЗОРИ охлажденная на подложке – бренд «Ясные зори» известен широким ассортиментом продукции, ее высоким качеством и доступной стоимостью. По статистике, курятина является излюбленным мясом россиян, намного опережая идущую вслед за ней свинину. Не удивительно, ведь при минимальных финансовых вложениях покупатель получает продукт, отличающийся высокой питательностью, замечательными вкусовыми характеристиками и простотой приготовления.', 'Голень куриная', '6addf1b328.jpg', '172.80', 3, b'1', 3),
(18, 'Окорочок утенка Утолина Дарфреш с кожей', '0,5 кг', '\"Утолина\" предлагает качественное мясо утки, выращенное на крупнейшем агрокомплексе, расположенном в безопасном и экологически чистом районе на территории Ростовской области. Утиное мясо считается одним из наиболее ценных поставщиков полноценного белка и отличным источником витаминов группы B (В1, В3, В6, В12, рибофлавина, фолиевой кислоты), помимо этого, мясо богато селеном, фосфором, медью, калием, железом, марганцем, натрием и другими элементами, необходимыми для организма человека.', 'окорочок утенка с кожей', 'f93898513f5.jpg', '218.99', 3, b'1', 0),
(19, 'Грудка куриная КУРИНОЕ ЦАРСТВО на кости с кожей', '1,2 к', 'Грудка куриная КУРИНОЕ ЦАРСТВО на кости с кожей – это прекрасный вариант для приготовления повседневного и праздничного обеда. Продукт отлично подходит для создания аппетитных наваристых супов, вторых блюд и салатов. Может выступать в роли начинки для домашней выпечки и клубных сэндвичей. Куриную грудку можно приготовить в пароварке, кастрюле, на сковороде и в духовом шкафу. Мясо курицы является очень питательным, богатым белком и различными полезными микроэлементами. Куриная грудка идеально сочетается с такими гарнирами, как макароны, мятая картошечка, гречневая каша и рис. Продукт можно фаршировать черносливом, яблоками и ананасом.', 'Мясо цыпленка, комплексная пищевая добавка, Е503, фосфаты ', '30ab70851.jpg', '228.06', 3, b'1', 0),
(20, 'Филе Петелинка без кожи', '830 г', 'Филе куриной грудки Петелино', 'Филе куриной грудки', '953cb3a7416fc.jpg', '236.18', 3, b'1', 0),
(21, 'Окорочок куриный с кожей Петелинка охлажденный', '1,3 кг', 'Окорочок ТМ ПЕТЕЛИНКА с тонкой кожицей и сочным натуральным мясом прекрасно подойдет для создания первых и вторых блюд. Простор для приготовления курицы по-настоящему безграничен. Окорочок можно подавать на стол в вареном, жареном или тушеном виде. Идеально подходит для приготовления на гриле. Куриное мясо отлично сочетается с белыми винами.', 'Окорочок куриный с кожей', 'f316c687ba3b.jpg', '252.72', 3, b'1', 20),
(22, 'Тушка цыпленка-бройлера Сельские традиции 1 сорт охлажденная', '2.4 кг', 'Мясо цыплят-бройлеров 1 сорта охлажденное (фирменный пакет)', 'Тушка цыпленка-бройлера', '61f3d77c463.jpg', '256.32', 3, b'1', 0),
(23, 'Голень куриная с кожей Петелинка охлажденная', '900 г', 'Голень куриная с кожей ПЕТЕЛИНКА станет частью ваших любимых домашних блюд. Она получена от цыплят-бройлеров, выращенных без инъекций и искусственных кормов. Нежное мясо приятно удивит вас натуральным вкусом и цветом. Мягкая кожа при запекании приобретает приятный золотистый оттенок и начинает хрустеть. Диетические достоинства курицы давно известны. Она богата витаминами и минеральными соединениями, которые делают ее употребление в пищу не только приятным, но и полезным. Благодаря высокому уровню белка в составе этого мяса оно рекомендовано при заболеваниях почек, печени и сердечно-сосудистых осложнениях.', 'Голень куриная с кожей', 'e9254caf4a2.jpg', '259.19', 3, b'1', 0),
(24, 'Тушка Петелинка цыпленка бройлера', '1.7 кг', 'Тушка цыпленка-бройлера Петелинка охлажденная, 1,4-1,9кг прекрасно подходит для приготовления, как первых, так и вторых блюд. Вы можете использовать еще для того, чтобы сделать изумительно вкусный бульон, теплота которого согревает не только тело, но и душу. К тому, же вы можете запечь эту тушку в рукаве или просто натереть карри и сделать такую курицу, изысканность которой понравилась бы самим индийцам. Цыплята-бройлеры данной торговой марки получают только натуральные корма, благодаря чему их мясо такое нежное и сочное.', 'Тушка цыпленка-бройлера', 'ec36835f16cd.jpg', '264.18', 3, b'1', 0),
(25, 'Бедро индейки', '0,9 кг', 'Бедро индейки прекрасно подходит для выходных, когда на готовку мало времени. Хорошо сочетается с пастой, с рисом, с гречкой, картофельным пюре и тушенными овощами.', 'Бедро индейки', 'f9d817cf6.jpg', '291.06', 3, b'1', 0),
(26, 'Грудка куриная с кожей Петелинка охлажденная', '1.5 кг', 'Грудка Петелинка цыпленка-бройлера с кожей. ', 'Грудка куриная с кожей', '47a15d858a.jpg', '326.03', 3, b'1', 0),
(27, 'Медальон индейки Краснобор охлажденный', '0,8 кг', 'Удивите гостей, приготовив им в духовке, гриле или сковороде медальон из индейки КРАСНОБОР Халяль. Этот продукт порадует всех своим нежным вкусом и умопомрачительным ароматом. ', 'медальон из индейки', 'b2ff7d639cc.jpg', '385.56', 3, b'1', 0),
(28, 'Бургеры Мираторг из говядины охлажденные', '200 г', 'Полуфабрикат мясной из говядины рубленый формованный охлажденный категории Б', 'говядина, вода, соль поваренная, перец черный', 'ef9b52707b.jpg', '117.60', 4, b'1', 3),
(29, 'Колбаски МИРАТОРГ чевапчичи из говядины охлажденные', '300 г', 'Колбаски МИРАТОРГ чевапчичи из говядины охлажденные, 300г – рецептура этих колбасок родилась на Балканах, а конкретно, в Черногории. Их изготавливают из рубленого мяса и подают уложенными горкой на разогретой тарелке. Чевапчичи считаются идеальным гарниром к жареному картофелю и овощам. «Мираторг» соблюдает классическую рецептуру и готовит колбаски из говядины сорта «Блэк Ангус», широко разрекламированный в последнее время, и душистых специй.', 'Говядина, вода, поваренная соль, декстроза, специи, лук, чеснок, паприка,стабилизатор Е331, Е450, Е500, регулятор кислотности Е451, Е452,загуститель Е415.', 'c29c0e7faada.jpg', '151.20', 4, b'1', 1),
(30, 'Фарш МИРАТОРГ По-домашнему', '500 г', 'Значительно сэкономить время на приготовлении мясных блюд вы сможете, используя фарш МИРАТОРГ по-домашнему. В качестве сырья для изготовления продукта использовалось отборное мясо двух видов: свинина и говядина.Производитель ответственно подходит к качеству своей продукции: в упаковке с фаршем вы не найдете воды, частиц костей, кожи и хрящей. Цвет полуфабриката насыщенный красный, консистенция – мягкая и однородная без больших жировых вкраплений.Фарш полностью готов к дальнейшей термической обработке. Продукт герметично упакован в удобный пластиковый лоток, что сохраняет свежесть на протяжении всего срока годности. Из полуфабриката можно приготовить сочные котлеты, мясное рагу, макароны по-флотски, добавить в пиццу, блинчики, голубцы и пельмени.', 'Свинина, говядина.', '40d3abdc.jpg', '166.80', 4, b'1', 0),
(31, 'Купаты Останкино Московские свиные охлажденные', '640 г', 'Купаты Московские свиные ОСТАНКИНО, можно приготовить на решетке-гриль или сковороде. Они являются хорошей заменой мяса на пикнике. Если вам не хочется тратить время на маринование, то приобретайте купаты, их вкус порадует всех.', 'свинина, соль, горчица, сахар, усилитель вкуса Е621', '82c0bd8720.jpg', '238.80', 4, b'1', 1),
(32, 'Фарш бараний Эколь халяль охлажденный', '0.7 кг', 'Компания «Эколь» производит халяльные мясные продукты и одновременно с этим является одним из лидеров по производству высококачественных мясных продуктов в России.', 'Баранина', 'be9a8106c6dc.jpg', '258.72', 4, b'1', 2),
(33, 'Грудинка свиная МИРАТОРГ без кости в специях', '1,4 кг', NULL, 'Грудинка свиная, вода питьевая, соль поваренная пищевая,натуральные специи и пряности: лук, петрушка, сахар', '73fbc399b6.jpg', '508.62', 4, b'1', 1),
(34, 'Фарш говяжий Останкино охлажденный', '1 кг', 'Останкинский мясоперерабатывающий завод, более известный на просторах бывшего Союза как мясокомбинат «Останкино», существует на рынке уже более полувека и ежегодно совершенствует своё мастерство в выпуске мясной продукции из экологически чистого мяса отечественного производства.', 'говядина', 'd4e5eb7b.jpg', '437.85', 4, b'1', 1),
(35, 'Стриплойн говяжий', '1,8 кг', 'Охлажденный отруб говядины в вакуумной упаковке. Срезается тонкий край стриплойн вдоль позвоночника из спинной части задней половины туши бычков, содержавшихся на комбинированном, травяно-зерновом откорме. Именно из этого вида мяса получается сочный, красивый, внушительный стриплойн стейк, способный покорить сердце каждого настоящего мужчины, вызвать восхищение истинного гурмана. Для того чтобы получить идеальный результат, говядину нужно нарезать пластинами толщиной 2 сантиметра, обеспечить среднюю степень прожарки, которую знатоки называют «медиум». Приготовление сочного, ароматного блюда не требует большого опыта, поэтому стриплойн купить может даже начинающий кулинар. Испортить это мясо невозможно.', NULL, '3cd902f0be8628.jpg', '3129.86', 4, b'1', 1),
(36, 'Цыплята табака Рококо в обсыпке', '1.05 кг', 'Цыплята-табака в обсыпке, охлажденные', 'мясо цыплят бройлеров, ароматическая соль для гриля \"Специаль\"', '487a28560.jpg', '192.94', 5, b'1', 2),
(37, 'Котлеты Пава Пава из мяса индейки охлажденные', '0,5 кг', 'В индейке «Пава-Пава» содержится в два раза меньше жира, чем в обычной индейке других производителей', 'Мясо индейки', NULL, '202.65', 5, b'1', 1),
(38, 'Огузок говяжий', '1 кг', 'Огузок говяжий. В коробе от 1 до 5 отрубов. ', 'Говядина', '70703b4f2a59039.jpg', '538.80', 6, b'1', 1),
(39, 'Корейка ягненка 4 ребра', '0,4 кг', 'Оцените по достоинству настоящий деликатес – корейку ягненка. Мясо считается низкокалорийным диетическим и рекомендовано для людей с избыточным весом. Корейка ягненка отличается нежным вкусом и ароматом, который вы оцените по достоинству. Мясо легко усваивается, поэтому вы можете включить его даже в детский рацион. ', NULL, '33d39216b01e.jpg', '1057.56', 6, b'1', 0),
(40, 'Баранина для шашлыка замороженная', '2,1 кг', 'Австралийская баранина высшего качества, подходит для жарки, тушения, варки, запекания. Полуфабрикат из баранины замороженный. Мясной, натуральный, мелкокусковой категории B. Произведено из замороженного мяса. Подвергнуть термической обработке до кулинарной готовности. Упаковано под вакуумом.', 'Баранина', '15f96a5b750.jpg', '1644.93', 6, b'1', 0),
(41, 'Нога ягненка Horizon без костреца замороженная', '1,7 кг', 'Нога ягненка без кости чаще всего используется для запекания или для гриля. Очень сочное и ароматное мясо.<br />Ягнятина - уникальный по своей универсальности вид мяса. Благодаря нежному вкусу и необычайно тонкому аромату ягнятина единодушно признается гурманами одним из лучших и чистейших мясных продуктов.', 'нога ягненка, мякоть', '69a62b24534a.jpg', '1934.96', 6, b'1', 0),
(42, 'Вырезка барашка', '1 кг', 'Вырезка барашка не только полезный и питательный продукт, имеющий потрясающие вкусовые качества. Это еще универсальный ингредиент для создания экзотических блюд. Баранина широко распространена в индийской кухне. Там она подается с цветной капустой или абрикосами. Также это красное мясо может сочетаться с французской горчицей, сливочным соусом на основе йогурта, кориандром или овощами. Баранина, запеченная на гриле, с добавлением специй имеет яркий, пряный вкус и аромат дымка.Вырезка барашка содержит большое количество витамина В12, способствующего активизации иммунной системы организма.', 'Баранина', 'e8b4f628324f4a.jpg', '2397.16', 6, b'1', 0),
(43, 'Грудинка Заречное говяжья мраморная', '6.780 кг', 'Благодаря высокому содержанию жира и соединительной ткани, отруб идеально подходит для варки или тушения. Высококачественная мраморная говядина из бычков мясо-молочных пород.  Бычки растут на фермах в Воронежской и Калужской областях, известных своей экологией. ', 'Говядина', 'b51ed1e9f76f3.jpg', '3424.17', 6, b'1', 0),
(44, 'Наружняя часть бедра говядины Праймбиф мраморная замороженная', '10 кг', 'Мраморная говядина – это дорогой и редкий деликатес. Это мясо поразит Вас своим вкусом и ароматом, не говоря уже про его привлекательный внешний вид.Однако из мраморной говядины есть большое количество вкусных альтернативных отрубов, которые будут более привлекательны в цене. Один из таких видов мяса – наружная часть бедра.  Это кусок мякоти из тазобедренного отруба, который сочетает в себе четыре вида различных мышц: среднеягодичная мышца, четырехглавая, сросшиеся полупоперечные и приводящие. ', 'Говядина', '05bd81d5b6e2.jpg', '5281.50', 6, b'1', 3),
(45, 'Окорок Заречное говяжий верхняя часть', '7 кг', 'Окорок говяжий верхняя часть Заречное благодаря мягкому сочному мясу превосходно подходит для приготовления кулинарных изысков: у вас легко получатся отбивные, бифштексы, стейки и проч. Основное применение продукт нашел в итальянской и французской кухне. Каждый уважающий себя ресторатор включает в меню своего ресторана блюда из говядины – диетическое мясо имеет большую популярность и позволяет быстро восстановить силы и наполнить организм энергией.', 'Говядина', 'c11b35f814c.jpg', '3969.00', 6, b'1', 0),
(46, 'Желудки куриные КУРИНОЕ ЦАРСТВО', '1 кг', 'Торговая марка «Куриное Царство» в сегменте замороженного мяса птицы занимает лидирующие позиции на федеральном уровне. Для линейки замороженной продукции применяется технология шоковой заморозки, которая позволяет сохранять максимум витаминов и питательных веществ.', 'Желудки цыплят бройлеров', '95efde5c55a.jpg', '137.55', 7, b'1', 2),
(47, 'Печень куриная КУРИНОЕ ЦАРСТВО', '1 кг', 'Печень куриная КУРИНОЕ ЦАРСТВО является основой для многих блюд. Она имеет специфический вкус и ценится как полезный, питательный продукт. Куриная печень используется для приготовления горячих блюд. Для этого ее жарят, запекают или тушат с овощами или различными соусами. Из печени делают превосходные паштеты, домашнюю колбасу или печеночный торт.Печень куриная КУРИНОЕ ЦАРСТВО производят на современном оборудовании из молодой птицы. В этом секрет ее нежности, естественного цвета и изысканного вкуса. Ее регулярное употребление помогает нормализовать работу кровеносной системы предупредить инфаркт миокарда.', 'печень цыплят', 'c36ed4f3ec.jpg', '145.95', 7, b'1', 1),
(48, 'Язык свиной МИРАТОРГ охлажденный', '0,6 кг', 'АПХ «Мираторг» является крупнейшим отечественным производителем свинины и ведущим оператором на рынке мяса и полуфабрикатов в России, благодаря новейшим технологиям, высокому уровню автоматизации и биологической безопасности ферм.', 'Свинина', '4b5aabba29dd.jpg', '223.65', 7, b'1', 2),
(49, 'Сердце куриное КУРИНОЕ ЦАРСТВО', '1 кг', 'Торговая марка «Куриное Царство» в сегменте замороженного мяса птицы занимает лидирующие позиции на федеральном уровне. ', 'Сердце цыплят', '6721533b518a6.jpg', '248.86', 7, b'1', 1),
(50, 'Камбала без головы потрошеная', '1,1 кг', 'Камбала одна из самых полезных видов рыбы, кроме этого камбала обладает прекрасными вкусовыми качествами. В России - это одна из самых распространенных рыб. Многих отталкивает запах камбалы который образуется при жарке, от этого можно легко избавится - снять кожицу сделав надрез по всему периметру. Кожица снимается достаточно легко, без особых усилий. Содержит небольшое количество костей; подходит для жарки и запекания.', 'камбала', 'b3606abef300.jpg', '356.90', 8, b'1', 4),
(51, 'Тилапия филе Agama без кожи свежемороженое', '400 г', 'Филе тилапии богато полиненасыщенными жирными кислотами Омега-3, витаминами, незаменимыми аминокислотами, легко перевариваемыми усвояемыми белками, макро- и микроэлементами. Мясо этой рыбы превосходно на вкус, имеет нежную тающую текстуру.', 'Тилапия, вода (защитная ледяная оболочка)', '35be35656bc938e.jpg', '341.26', 9, b'1', 2),
(52, 'Нерка стейк свежемороженая', '500 г', 'Нерка стейк с/м, 500 г имеет упругое мясо насыщенного красного цвета, богатый вкус которого позволяет приготовить из нее замечательные блюда как для повседневного, так и для праздничного стола. Очень быстро готовится нерка в мультиварке и в духовке. Она содержит в себе практически все минералы и витамины, которые необходимы человеку для нормальной жизнедеятельности. Регулярно употребляя в пищу мясо этой рыбы, можно нормализовать обменные процессы, поддержать здоровье слизистых оболочек, восстановить оптимальный баланс между микроэлементами и питательными веществами.', 'Стейк нерки', '5350afcb2e155.jpg', '418.95', 9, b'1', 0),
(53, 'Кижуч стейк свежемороженый', '500 г', 'Содержит витамины A, E, PP, D и микроэлементы калий, кальций, фосфор', NULL, 'd322f114511.jpg', '427.15', 9, b'1', 1),
(54, 'Пикша филе Fine Life на коже свежемороженая', '1 кг', 'Пикша — третья по популярности тресковая рыба после трески и минтая. Пикша уже занесена в Международную Красную книгу, но ее лов, до сих пор производится. Рыба пикша подходит для приготовления огромного количества интересных блюд благодаря тому, что ее тонкий вкус отменно сочетается с разнообразными пряностями, многими овощами и остро-пикантными соусами. Упругое мясо отлично сохраняет свой привлекательный вид и полезные свойства пикши при любом способе термической обработки. К примеру, у приготовленной на пару и отваренной пикши необычайно белое слоистое филе, идеально подходящее для рациона питания людей, которые хотят похудеть.', 'Пикша (Melanogrammus aegltfinus), вода (защитный слой глазури 5%)', '781c80739030.jpg', '450.45', 9, b'1', 0),
(55, 'Барабулька МАГУРО свежемороженая', '800 г', 'Барабулька - это нежная деликатесная рыбка с большим количество высококалорийного белка, который легко усваивается. Кроме того, она очень питательна. Чаще всего из барабули варят уху, ее вялят, жарят на гриле. Благодаря отсутствию желчи, эту рыбу можно не потрошить и зажаривать целиком.', 'барабулька', '75bf025d82.jpg', '472.49', 9, b'1', 2),
(56, 'Треска филе Fine Life на коже свежемороженая', '1 кг', 'Бренд FINE LIFE был специально создан METRO для покупателей, желающих за невысокую цену приобрести товар высокого качества. Бренд предлагает широкий выбор продукции и пользуется все увеличивающейся популярностью у многих потребителей. Треска FINE LIFE филе на коже свежемороженая обладает слоистым белым мясом и содержит в себе множество необходимых для здоровья микроэлементов и витаминов. Благодаря особому способу заморозки, этот продукт практически не имеет наледи, поэтому можете быть уверены, что при приобретении его вы не потратите деньги на замороженную воду. Треска FINE LIFE филе на коже свежемороженая будет особенно хороша в пирогах, запеканках и салатах. Ее готовят на гриле и на пару, жарят с овощами и запекают с сыром. Это очень сытный и вкусный продукт, который понравится всем.', 'Треска', '7861eb667.jpg', '478.80', 9, b'1', 20),
(57, 'Кета ПОРТЪ филе на коже свежемороженое', '0,9 кг', 'Кета филе на коже ПОРТЪ свежемороженое, 1 кг – удобный в использовании и полезный натуральный морепродукт. Эта красная рыба с минимальным количеством жиров богата витаминами и кислотами омега-3. Ее употребление регулирует обмен веществ и замедляет процессы старения. Филе на коже идеально подходит для жарки и запекания, из него легко можно и приготовить фарш, и нарезать порционные кусочки. Рыба полностью готова к приготовлению. Заморозка в ледовой глазури позволяет сохранить внешние и вкусовые качества продукта.', 'кета, ледяная глазурь', '760d788fcff7.jpg', '603.86', 9, b'1', 2),
(58, 'Лосось FISH AND MORE стейк слабосоленый', '500 г', 'Лосось обитает в Атлантическом и Тихом океанах, а также в пресных водах Северного полушария, в средних и северных широтах. Крупнейшие естественные нерестилища лососевых расположены на Камчатке,Сахалине и Курилах.  Эта рыба богата жирными кислотами омега-3, благодаря которым наш организм получает защиту от развития воспалительных процессов. Помимо этого они они способны предотвращать образование кровяных сгустков и тромбов, которые являются причиной инфарктов. Красная рыба очень приятна на вкус абсолютно в любом виде, хороша она во всех видах: маринованном, соленом, копченом, жареном. В копченом или соленом виде она всегда выручит, когда неожиданно на пороге появляются гости: приготовить бутерброды, рулетики, тосты с ней – дело 5 минут. В Италии рыбу лосось (семгу) обжаривают в кляре, в Японии из нее делают суши, под маринадом ее готовят в Финляндии', NULL, 'be3e9ed14a7.jpg', '628.95', 9, b'1', 1),
(59, 'Нерка тушка потрошеная без головы свежемороженая', '0,8 кг', 'Нерка - дикий лосось - принадлежит к семейству тихоокеанских лососевых рыб. Вкусовые качества этой рыбы намного богаче, чем остальных представителей рыб лососевых, напримар, кеты или горбуши. Благодаря высокой жирности мяса нерка служит идеальной основой для изготовления копченостей, в частности отменных балыков. Очень вкусно готовят рыбу нерка на пару, а также используют для приготовления рыбных салатов, а также холодных закусок.', 'Нерка без головы', '2888bb66c05.jpg', '671.99', 9, b'1', 3),
(60, 'Тунец филе Agama свежемороженое', '400 г', 'Тунец - один из самых популярных морских обитателей. Он любим в Японии, Америке, Европе, а в последнее время и в России. Его мясо, имеющее характерный красный цвет на срезе, часто сравнивают с говядиной или телятиной. Это обусловлено тем, что по консистенции мясо тунца плотное и очень вкусное. Оно насыщено белком, по содержанию которого тунец - настоящий рекордсмен, а также кислотами Омега-3 и Омега-6, витаминами, минералами и другими ценными веществами. Таким образом, польза этой рыбы превосходит все ожидания. Его можно жарить, тушить, варить, добавлять в суп и консервировать, а также готовить на гриле.', 'Тунец, вода (глазурь)', '529a2c906a6.jpg', '774.90', 9, b'1', 0),
(61, 'Семга филе Fishka без кожи порционное свежемороженая', '500 г', 'Филе семги без кожи ФИШКА порционное свежемороженое обладает сочным нежным вкусом и приятным ароматом. Рыба содержит множество полезных веществ и витаминов. Продукт может использоваться для приготовления различных салатов и блюд, включая супы. Благодаря низкой калорийности, рыбу можно включать и в диетическое меню. Метод приготовления зависит от предпочтений кулинара: лосось может быть запечен, прожарен, украшен зеленью и приправлен соусом. Удобная упаковка в виде пластикового пакета помогает хранить товар свежим в течение продолжительного времени.', 'Семга, глазурь (вода) не более 5%', 'a03c90d8fb155.jpg', '838.95', 9, b'1', 0),
(62, 'Треска филе без кожи свежемороженое', '3 кг', 'Филе трески ОКЕАН ВКУСА без кожи свежемороженое отличается неповторимым нежным вкусом и сочностью. Продукт богат белком, витаминами и минеральными веществами. Рыба добывается в чистых океанских водах и проходит специальную обработку. Употребление продукта способствует нормализации обмена веществ, укреплению защитных функций организма, костей и зубов. Филе подходит для варки, жарки или тушения. В сочетании с овощами, зеленью и изысканными приправами станет настоящим украшением праздничного стола.', 'Филе трески', '75994ff15c5.jpg', '1571.85', 9, b'1', 0),
(63, 'Кета тушка', '3.7кг', 'Кета потрошеная без головы свежемороженая', NULL, 'f9da45f2ba3af.jpg', '1822.07', 9, b'1', 0),
(64, 'Килька пряного посола Балтийская', '400 г', 'Килька Балтийская пряного посола имеет неповторимый вкус и аромат специй. Необычное сочетание этих ингридиентов придает рабе пикантные нотки. Вы получите незаменимую закуску к любому гарниру или праздничному столу.', 'Рыба, соль, сахар, пряности натуральные, консерванты Е211, Е202', '03b17838329.jpg', '93.45', 10, b'1', 3),
(65, 'Мойва Aro холодного копчения', '300 г', 'Мойва Aro холодного копчения. Упаковано под вакуумом.', 'Мойва (Mallotus villosus), соль', '28035cbe3704.jpg', '111.30', 10, b'1', 0),
(66, 'Анчоус Балтийский Берег в масле', '145 г', 'Анчоус Балтийский Берег в масле станет незаменимым атрибутом домашних бутербродов или отличной холодной закуской. Его можно добавлять в пиццу, салаты делать соусы и паштеты на его основе. Это продукт, произведенный из отборной рыбы с добавлением пряностей, которые придают ему изысканный вкус. Сырье обрабатывают при помощи высокотехнологичного оборудования, которое позволяет сохранять его полезные свойства.', 'Килька пряного посола, масло подсолнечное рафинированное дезодорированное, соль, сахар, пряности натуральные, консерванты Е211, Е202', 'd41c01afba1a.jpg', '145.96', 10, b'1', 2),
(67, 'Пресервы рыбные анчоус и килька с оливками', '145 г', 'Анчоусы с оливками. Продукт готов к употреблению.', 'Килька пряного посола, масло подсолнечное рафинированное дезодорированное, оливки консервированные, соль, сахар, пряности натуральные, консерванты Е211, Е202', 'c5e072cb0c2baf.jpg', '145.96', 10, b'1', 1),
(68, 'Масляная рыба МЕРИДИАН ломтики холодного копчения', '120 г', 'Масляная рыба характеризуется высокой калорийностью, является источником энергии, так необходимой людям, ведущим активный образ жизни или испытывающим тяжелые физические нагрузки. В ней содержится много ценного белка, витамина РР и хрома, играющих одну из основных ролей в работе эндокринной системы.', 'масляная рыба, соль', '9325a88b6da92.jpg', '198.45', 10, b'1', 6),
(69, 'Горбуша слабосоленая нарезка', '150 г', 'Горбуша Олива Факел ломтики слабосоленая. Является отличной закуской, а также хорошо сочетается с другими морепродуктами. Можно добавлять в салаты либо готовить самостоятельные блюда. Вакуумная упаковка сохранит всю свежесть продукта и его натуральный вкус.', 'горбуша, соль, консервант Е211', 'bbd59f251467d.jpg', '208.95', 10, b'1', 0),
(70, 'Корюшка холодного копчения', '250 г', 'Как продукт питания, корюшка имеет большой набор микроэлементов, таких как калий, железо, натрий, магний, фтор, молибден, хлор и фосфор. Также корюшка содержит много витаминов, особенно группы В, витамины A, D и другие. Людям пожилого возраста эта рыба будет особенно полезна.', 'Корюшка, соль, консервант Е211', 'f51eee0dd1b80.jpg', '231.00', 10, b'1', 0),
(71, 'Вобла Fine Life вяленая', '300 г', 'Любители пива, наверняка, любят и вяленую сушеную рыбку. И, конечно же, знают о вкусовых качествах самой популярных к пенному напитку закуски - воблы. Вобла вяленая послужит прекрасным дополнением к хмельному напитку и станет центром внимания всей компании. Уже подсоленную рыбу затем выносят в специальный цех, где она проходит процесс вяления и постепенно обезвоживается, приобретая свой конечный вид. Воблу вяленую можно употреблять в пищу без предварительной обработки. Кроме того, она в течение длительного времени может сохранять свой вкус и качество.', 'Рыба - вобла, соль', '3285432eb72864.jpg', '173.25', 10, b'1', 0),
(72, 'Форель Fine Life слабосоленая', '120 г', 'Форель слабосоленая в вакуумной упаковке удивительным образом сохраняет все свои достоинства: аромат все такой же душистый и насыщенный, а вкус неизменно нежный с неповторимым послевкусием. Необходимые человеку полезные вещества практически полностью остаются в первоначальном виде, обогащая организм и заряжая энергией.', 'филе форели, соль', '03b7fd3db240ec.jpg', '241.50', 10, b'1', 0),
(73, 'Нерка Меридиан подкопчённая ломтики', '120 г', 'Нерка МЕРИДИАН ломтики – восхитительный продукт, качество которого подтверждено сертификатом ЭКО-ТЕСТ-Плюс. Вы не сможете устоять перед сочетанием пикантного вкуса и аромата слабосоленой подкопченной нерки. Продукт изготовлен из натуральных ингредиентов без добавления ГМО и консервантов. Благодаря невысокой калорийности насладиться удивительным вкусом могут даже те, кто придерживается диет и следит за фигурой. Нерка является прекрасной самостоятельной закуской. Кроме того, она прекрасно сочетается с различными гарнирами. ', 'рыба, соль', 'd32cf7cbce0c2.jpg', '313.95', 10, b'1', 5),
(74, 'Угорь Fine Life ломтики холодного копчения', '150 г', 'Угорь Fine Life ломтики холодного копчения. Упаковано под вакуумом.', 'рыба, соль', '805058701cf13.jpg', '320.26', 10, b'1', 3),
(75, 'Минога горячего копчения', '200 г', 'Минога Лисий Нос горячего копчения, 200г поможет удивить гостей и станет украшением стола. Рыбу производят с использованием специальной разработанной технологии копчения, одним из элементов которой является добавление можжевеловых веток. Это не только придает рыбе неповторимые вкусовые оттенки и аромат дымка, но и помогает дольше сохранять свежесть. Рыбу обрабатывают сразу после вылавливания. Минога известна особым химическим составом своего мяса. Это очень жирная рыба, богатая минеральными веществами.', 'Минога, соль, консервант Е211 (бензоат натрия)', 'd8b2c9f15833.jpg', '330.76', 10, b'1', 0),
(76, 'Корюшка Fine Life вяленая', '250 г', 'Вяленая рыбка используется как самая ходовая закуска, особенно к пиву. Вяленую рыбу можно также отварить (положить в кипящую воду на 10-20 мин.) или запечь в духовке (в посуде с крышкой).', 'Рыба корюшка, соль', 'bafb2e0626af5.jpg', '346.50', 10, b'1', 0),
(77, 'Масляная рыба Fine Life филе холодного копчения в вакуумной упаковке', '300 г', 'Рыба масляная филе-кусок холодного копчения в вакуумной упаковке. Это уже полностью готовый продукт, который в кулинарных целях можно не только добавлять в салаты или делать с ней бутерброды, но и есть ее просто так, без хлеба.', 'Масляная рыба (Lepidocybium flavobrunneum), соль', '2b8773ef1cba.jpg', '358.80', 10, b'1', 1),
(78, 'Палтус Fine Life холодного копчения кусок с кожей', '300 г', 'Палтус Fine Life х/к филе отлично подойдет для приготовления легких закусок и салатов. Современные технологии копчения позволяют придавать мясу восхитительный вкус с нотками дымка. При его производстве используют только свежую рыбу. Такой подход дает возможность достигать высокого качества продукта. Филе великолепно подойдет к прохладительным напиткам. Его свежесть сохраняется благодаря вакуумной упаковке. Палтус Fine Life х/к филе лучше подавать нарезанным тонкими ломтиками со свежими овощами и зеленью.', 'Палтус, соль, консервант Е211', '9d6966f61cd9097.jpg', '454.80', 10, b'1', 0),
(79, 'Кижуч Меридиан ароматный со специями горячего копчения', '200 г', 'Кижуч Меридиан ароматный со специями горячего копчения . Упаковано под вакуумом.', 'кижуч, соль, специи', 'b114f97efd.jpg', '502.96', 10, b'1', 1),
(80, 'Форель Fine Life филе кусок малосольная', '300 г', 'Форель малосольная в вакуумной упаковке удивительным образом сохраняет все свои достоинства: аромат все такой же душистый и насыщенный, а вкус неизменно нежный с божественным послевкусием. Необходимые человеку полезные вещества практически полностью остаются в первоначальном виде, обогащая организм и заряжая энергией.', 'филе форели, соль', '8416ad0200a766.jpg', '526.81', 10, b'1', 3),
(81, 'Семга филе-кусок слабосоленая', '250 г', 'Нежное филе семги, приготовленное по традиционному рецепту, имеет приятный солоноватый вкус. Продукт богат жирными кислотами Омега-3 и станет прекрасной закуской к праздничному столу.', 'семга, соль', 'ad94f8396c76a.jpg', '576.46', 10, b'1', 0),
(82, 'Форель Меридиан Эко филе-кусок слабосоленая', '250 г', 'Форель Меридиан Филе-кусок слабосоленая 250г. Экологическая безопасность и высокое качество продукта подтверждены. Нежное филе, приготовленное по традиционному рецепту. Продукт богат жирными кислотами Омега-3, способствующими укреплению сердечно - сосудистой системы.', 'форель, соль', 'ba8d843c198.jpg', '597.56', 10, b'1', 0),
(83, 'Горбуша Меридиан соломка копченая', '80 г x 3 шт', 'Копченую соломку из филе горбуши можно смело назваться одной из самых универсальных закусок. Почему? Все предельно просто. Они могут смело использоваться не только в качестве закуски к пиву. Вяленая соломка горбуши применяется в качестве одного из ингредиентов к различным салатам. Отменное вкусовое качество делает горбушу самой популярной закуской, обладающей многими полезными витаминами и микроэлементами.', 'Филе горбуши дальневосточной, соль', 'a3184f8ce2e.jpg', '618.46', 10, b'1', 0),
(84, 'Горбуша Меридиан соломка копчено-вяленая пикантная', '80 г x 3', 'Копченую соломку из филе горбуши можно смело назваться одной из самых универсальных закусок. Почему? Все предельно просто. Они могут смело использоваться не только в качестве закуски к пиву. Вяленая соломка горбуши применяется в качестве одного из ингредиентов к различным салатам. Отменное вкусовое качество делает горбушу самой популярной закуской, обладающей многими полезными витаминами и микроэлементами.', 'Филе горбуши дальневосточной, соль, чеснок, паприка', 'f31f3873c992a.jpg', '618.46', 10, b'1', 0),
(85, 'Форель Русское море Подкопченная кусочками', '300 г', 'Форель Русское море Подкопченная кусочками. Изысканный деликатес.', 'рыба, соль, консервант Е 211', 'e83bdd0e0db.jpg', '630.01', 10, b'1', 0),
(86, 'Омуль холодного копчения кусочки', '500 г', 'Омуль холодного копчения кусочки – это изысканный вкус «царской рыбы», богатой витаминами и микроэлементами, и новейшие технологии приготовления, призванные подчеркнуть и сохранить уникальный вкус и свойства деликатесного продукта. Его можно употреблять в пищу непосредственно после вскрытия упаковки с разнообразными гарнирами и напитками. Диетическое мясо этой рыбы легко усваивается в организме, быстро вовлекая полезные вещества в обменные процессы.', 'Рыба, соль, консервант - Е211', '887f647189e.jpg', '733.96', 10, b'1', 0),
(87, 'Икра Камчадал судака соленая', '120 г', 'Икра судака-очень вкусный деликатес. Отличная закуска для любого стола. Содержит витамины, которые очень необходимы организму.', 'Икра судака, соль, масло подсолнечное, консервант: Е211 (бензоат натрия)', '686319a75f5.jpg', '82.95', 11, b'1', 2),
(88, 'Икра мойвы Санта Бремор деликатесная подкопченная', '180 г', 'Икра мойвы Санта Бремор подкопчённая – полезна, и быстроусвояема. В икре мойвы сконцентрирован комплекс аминокислот, а также самые необходимые витамины. Среди них – почти все витамины B-группы, витамины E, D, A. Продукт содержит широкий спектр минеральных веществ: фосфор, калий, йод, кальций, цинк, железо, магний и других. Икра мойвы Санта Бремор подкопчённая – основной ингредиент различных бутербродов и тарталеток.', NULL, '55c858680d044.jpg', '98.16', 11, b'1', 1),
(89, 'Икра Красное Золото минтая пробойная', '130 г', 'Икра минтая-очень вкусный деликатес. Отличная закуска для любого стола. Содержит витамины В2 -0,2мг, РР -0,7мг, которые очень необходимы организму.', 'Икра минтая, соль, масло растительное,_x000D_ консервант Е211 (бензоат натрия)', 'aa2b8657740.jpg', '99.76', 11, b'1', 0),
(90, 'Икра Здоровье лососевая имитированная', '500 г', 'Икра \"Здоровье\" - это вкусный и полезный продукт для приверженцев здорового образа жизни! Икра \"Здоровье\" производится на основе морских водорослей, по содержанию полезных веществ превосходящих все произрастающие на земле растения и обладающих целебными для нашего организма свойствами', 'Натуральный рыбный бульон, соль пищевая, масло подсолнечное, загустители: экстракт бурых водорослей (альгинат натрия), экстракт красных водорослей ( агар), Е466, рыбий жир, специи, красители: экстракт паприки, кармин, консервант: сорбат калия, регулятор кислотности: молочная кислота', '66e6066094aa0.jpg', '100.05', 11, b'1', 1),
(91, 'Икра Русское Чудо лососевая иммитированная', '120 г', 'Икра иммитированная производится на основе морских водорослей. В этих чудо-растениях есть все для здоровья и долголетия: йод, витамины, омега-3 кислоты, ценные микроэлементы. Этот деликатесный продукт, по своим вкусовым качествам почти не отличимый от натуральной икры благородных лососевых сортов рыбы.', 'Натуральный рыбный бульон, мясо лососевых рыб, соль пищевая, масло подсолнечное, загустители: экстракт бурых водорослей (альгинат натрия), ксантановая камедь, рыбий жир пищевой, специи, красители: кармин и бета-каротин, регулятор кислотности: молочная кислота', '69b1fc3b3.jpg', '110.26', 11, b'1', 1),
(92, 'Икра Щуки пробойная окрашенная в стеклянной банке', '100 г', 'Икра щуки - это очень полезный и вкусный продукт. В ней содержаться витамины и минеральные элементы. Пробойная слабосоленая икра щуки позволит Вам приготовить восхитительные блюда, которые займут особое место на Вашем праздничном или повседневном столе.', 'Икра щуки солено-мороженая, соль, растительное масло, консерванты Е200, Е211', '323236d998612.jpg', '188.91', 11, b'1', 0),
(93, 'Икра Fine Life лососевая горбуши зернистая', '100 г', 'Икра лососевая горбуша Fine Life – это не только показатель достатка в доме, но и один из самых полезных продуктов. Для того чтобы получить суточную дозу витаминов, человеку необходимо съесть всего лишь небольшую порцию лососевой икры. Эта зернистая икра всегда украсит стол и порадует гостей на любом празднестве, придавая ценность всему угощению.', 'Икра горбуши, соль, масло растительное, консерванты Е200, Е211', 'c8dfdeea27c.jpg', '576.46', 11, b'1', 0),
(94, 'Икра лососевая Обкомовская зернистая', '140 г', 'Отборная красная икра отличного качества от признанного российского производителя. Самое популярное лакомство для праздничного стола. Лососевая икра содержит множество полезных элементов и витаминов.', 'Икра лососевая зернистая, соль, масло растительное, консерванты Е200, Е211', '5de718a91a22e.jpg', '722.99', 11, b'1', 0),
(95, 'Икра Fine Life лососевая зернистая', '220 г', 'Икра горбуши Fine Life создаст праздничную атмосферу за вашим столом. Кроме того, традиционный для застолья обладает множеством полезных свойств. Икра очень питательна и богата белками, которые легко усваиваются в организме и способствуют укреплению стенок сосудов. В ней содержится много микроэлементов и витаминов различных групп. Икра способствует профилактике атеросклероза, улучшению зрения и стабилизации кровообращения.', 'Икра горбуши (Oncorhynchus gorbuscha), соль, масло растительное, консерванты Е200, Е211', '01989419f9.jpg', '1258.96', 11, b'1', 2),
(96, 'Икра красная Красное золото форели', '230 г', 'ИКРА ФОРЕЛИ - икра рыбы семейства лососевых. Это ценный пищевой продукт, имеющий высокие вкусовые качества. Икра форели является традиционным русским деликатесом,за что она получила название «красное золото России». Является надежным источником сбалансированных витаминов группы В, С, Д и Е.', 'Икра форели, соль, масло рафинированное подсолнечное, консерванты: Е200 - сорбиновая кислота, Е211 - бензоат натрия', 'f65c2af4fd.jpg', '1258.96', 11, b'1', 0),
(97, 'Икра лососевая красная Камчатское Море горбуши нерки', '500 г', 'Лососевая икра нерки «Камчатское Море» в своем составе хранит большом количестве витаминов, аминокислот и железа. Этот полезный продукт нормализует обменные процессы и уровень сахара в крови. Небольшие темно-красные икринки обладают насыщенным запахом и приятным слегка горьковатым вкусом настоящего русского деликатеса. Икра нерки гармонично сочетается с румяными блинами и картофельными драниками.', 'Икра нерки, соль, масло подсолнечное, консерванты: Е200 (сорбиновая кислота), Е211 (бензоат натрия)', '353350e1e617.jpg', '2728.96', 11, b'1', 1),
(98, 'Палочки крабовые Меридиан охлажденные', '100 г', 'Крабовые палочки (также называемые «имитацией крабового мяса») — вид продуктов, которые создаются искусственно из обработанного рыбного белка сурими или измельчённого мяса белой рыбы. По форме и цвету напоминает мясо крабьей клешни или ноги.', 'Фарш рыбный сурими, вода, крахмал, соль, сахар, ароматизатор идентичный натуральному, усилитель вкуса Е621, пищевой краситель Е 160', '17fcfdcc798ae1.jpg', '44.51', 12, b'1', 0),
(99, 'Крабовые палочки Русское море охлажденные', '200 г', 'Крабовые палочки (имитация) Русское море охлажденные, 200г. Низкокалорийный. Для салатов. Для вторых блюд. Для сэндвичей. ', 'Фарш рыбный \"сурими\" (рыбный фарш, сахар, стабилизаторы консистенции Е450, Е451), вода, смесь крахмалов (картофельный, кукурузный, тапиоковый), масло растительное, соль, яичный белок, сорбит, усилитель вкуса глутамат натрия, аромат краба, мирин, красители пищевые натуральные: кармин, экстракт паприки)', '80106477b9014.jpg', '78.76', 12, b'1', 0),
(100, 'Палочки VICI крабовые', '200 г', 'Сочные, пикантные, плотные, но тающие во рту, они привнесут в Ваши блюда яркие рыбные оттенки. Крабовые палочки могут быть самостоятельной закуской, а также ингредиентом для сытных рулетов, легких сэндвичей с овощами и различных салатов - например, с огурцами, авокадо и грушей или с морковью, яйцами и чесночным майонезом.', 'фарш рыбный сурими, вода, крахмал пшеничный, масло рапсовое/подсолнечное, крахмал картофельный, крахмал тапиоковый модифицированный, соль, белок яичный, сахар, пищевые добавки: ароматизатор идентичный натуральному крабовый, красители: кармины, маслосмолы паприки', 'd17950efcf8a26.jpeg', '99.76', 12, b'1', 0),
(101, 'Паста из морепродуктов Антарктик-Криль подкопченый', '150 г', 'Нежнейшая паста-крем - отличная начинка для деликатесных блюд. Легкая воздушная консистенция тает во рту. Обладает приятным пикатным вкусом с ароматом чеснока.', 'масло подсолнечное, мясо криля, яичный желток, стабилизаторы, молочный белок, сахар, соль, чеснок, ароматизаторв', 'ecafd215974b4.jpg', '102.01', 12, b'1', 20),
(102, 'Крабовые палочки Vici Приорити охлажденные', '200 г', 'Натуральное крабовое мясо в составе делает вкус этих палочек столь насыщенным и богатым. Сочные, пикантные, плотные, но тающие во рту, они привнесут в Ваши блюда яркие рыбные оттенки. Крабовые палочки могут быть самостоятельной закуской, а также ингредиентом для сытных рулетов, легких сэндвичей с овощами и различных салатов - например, с огурцами, авокадо и грушей или с морковью, яйцами и чесночным майонезом.', 'Фарш рыбный сурими, вода, крахмал пшеничный, тапиоковый модифицированный крахмал, крахмал картофельный, масло рапсовое или подсолнечное, белок яичный, соль, сахар, пищевые добавки: ароматизатор идентичный натуральному крабовый, красители: кармины, маслосмолы паприки', '991fade36dcb6f.jpg', '103.95', 12, b'1', 0),
(103, 'Крабовые палочки Меридиан Снежный краб охлажденные', '200 г', 'Крабовые палочки  (также называемые «имитацией крабового мяса») — вид продуктов, которые создаются искусственно из обработанного рыбного белка сурими или измельчённого мяса белой рыбы. По форме и цвету напоминает мясо крабьей клешни или ноги.', 'Рыбный фарш \"сурими\", вода, картофельный крахмал, пшеничный крахмал, яичный белок, соль, масло растительное, сахар, \"мирин\", \"краб\" - ароматизаторы (идентичные натуральным), стабилизатор - Е170, красители натуральные Е120, Е160с, полифосфат - влагоудерживающий агент', '927604b6896b07c.jpg', '109.20', 12, b'1', 2),
(104, 'Мясо кальмара 5 Океан со вкусом краба', '70 г', 'Засушенное мясо краба с ароматом краба. Подойдет в качестве деликатеса или как закуска к пиву.', 'кальмар сушенный рваный, сахар, соль, Е-621 (глутамат натрия), вкусоароматическая пищевая добавка \"вкус краба\"', '35f6389136e8dd3b.jpg', '112.34', 12, b'1', 1),
(105, 'Мидии Fine Life Мехико', '200 г', 'Мидии в масле с пряностями. Продукт готов к употреблению.', 'Мидии (Mytilus chilensis), масло растительное, сахар, соль, регулятор кислотности: уксусная кислота, паприка, семена горчицы, перец черный, душистые травы, консерванты Е211, Е202', '97a70cba8b4e.jpg', '114.46', 12, b'1', 1);
